mapper.select:
SELECT a.content_no, a.content_nm, a.country, a.rating, a.thumbnail FROM tb_content a, tb_genre b, tb_content_genre c , tb_content_ott d, tb_ott e, tb_content_category f, tb_category g where a.content_no = c.content_no and b.genre_no = c.genre_no and a.content_no = f.content_no and a.content_no = d.content_no and d.ott_no = e.ott_no and f.category_no = g.category_no
and (a.content_nm like concat('%', #{content_nm}, '%'))
or e.ott_no in
#{ottno}
or c.genre_no in
#{gerneno}
or f.category_no in
#{categoryno}
group by a.content_no
limit #{SearchItem.pageSize} offset #{SearchItem.offset}

SELECT COUNT(DISTINCT a.content_no) FROM tb_content a, tb_genre b, tb_content_genre c, tb_content_ott d, tb_ott e, tb_content_category f, tb_category g WHERE a.content_no = c.content_no AND b.genre_no = c.genre_no AND a.content_no = f.content_no AND a.content_no = d.content_no AND d.ott_no = e.ott_no AND f.category_no = g.category_no
AND (a.content_nm LIKE CONCAT('%', #{content_nm}, '%'))
OR e.ott_no IN
#{ottno}
OR c.genre_no IN
#{gerneno}
OR f.category_no IN
#{categoryno}
